import React, { Fragment, useState } from 'react';

const EditProject = ({ project }) => {

    const [project_company_name, setProjectCompanyName] = useState(project.project_company_name);
    const [project_description, setProjectDescription] = useState(project.project_description);
    const [project_location, setProjectLocation] = useState(project.project_location);

  // Edit Project_Company_Name
  const updateInfo = async e => {
    e.preventDefault();
    try {
      const body = {project_company_name, project_description, project_location};
      const response = await fetch(`http://localhost:5000/project/${project.project_id}`, {
        method: "PUT",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(body)
      });

      window.location = "/";

    } catch (err) {
      console.error(err.message)
    }
  }
  
    return (
        <Fragment>
        <header>
          <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" />
            <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.12.4/jquery.min.js"></script>
            <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script>
        </header>
        <div>
          <button type="button" 
            class="btn btn-outline-success" 
            data-toggle="modal"  
            data-target={`#id${project.project_id}`}
            >
            Edit
          </button>

          {/*
            id = id10
          */}
          <div class="modal" id={`id${project.project_id}`}>
            <div class="modal-dialog">
            <div class="modal-content">

            <div class="modal-header">
              <h4 class="modal-title">Edit Project</h4>
              <button type="button" 
                class="close" 
                data-dismiss="modal"
                onClick={() => setProjectCompanyName(project.project_company_name) 
                && 
                setProjectDescription(project.project_description)
                &&
                setProjectLocation(project.project_location)}
                >&times;</button>
            </div>

            <div class="modal-body">
              <input type="text" 
                className={"form-control mt-3"} 
                value={project_company_name} 
                onChange={ e => setProjectCompanyName(e.target.value)}
                />
              <input type="text" 
                className={"form-control mt-3"} 
                value={project_description} 
                onChange={ e => setProjectDescription(e.target.value)}
                />
              <input type="text" 
                className={"form-control mt-3"} 
                value={project_location} 
                onChange={ e => setProjectLocation(e.target.value)}
                />
            </div>

            <div class="modal-footer">
              <button 
                type="button" 
                class="btn btn-success" 
                data-dismiss="modal"
                onClick = {e => updateInfo(e)}
              >
                Save</button>
              <button type="button" 
                class="btn btn-danger" 
                data-dismiss="modal"
                >
                Close</button>
            </div>

            </div>
            </div>
          </div>
          </div>
        </Fragment>
    )
};

export default EditProject;
