import React, { Fragment, useEffect, useState } from 'react';

const ListProject = () => {

    const [projects, setProjects] = useState([]);

    // Delete function
    const deleteProjects = async (id) => {
        try {
            const deleteProject = await fetch(`http://localhost:5000/project/${id}`, {
                method: "DELETE"
            });
            window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
        
    }

    // Fetch data function 
    const getProjects = async() => {

        try {
            const response = await fetch("http://localhost:5000/project");
            const jsonData = await response.json();

            setProjects(jsonData);  
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getProjects();
    }, []);

    return <Fragment>
    <header>
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" />
        <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.12.4/jquery.min.js"></script>
        <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script>
    </header>
        <h1 className={"text-center p-3"}>Project List</h1>  
        <table class="table mt-5 text-center">
            <thead>
                <tr>
                <th>Company Name</th>
                <th>Project Description</th>
                <th>Project Location</th>
                <th>Edit project</th>
                <th>Delete Project</th>
                </tr>
            </thead>
    <tbody>
        {/*
        Reference fyrir uppbyggingu töflu sem ég mun nota - Mun breytast eitthvað
        <tr>
            <td>Ístak ehf</td>
            <td>Doe</td>
            <td>john@example.com</td>
            <td>john@example.com</td>
            <td>john@example.com</td>
            <td>john@example.com</td>
        </tr>
        */ }
        {/* This is how I map to be able to use the data */}
        {projects.map(project =>(
            <tr key={project.project_id}>
                <td>{project.project_company_name}</td>
                <td>{project.project_description}</td>
                <td>{project.project_location}</td>
                <td>Edit</td>
            <td>
                <button className={"btn btn-danger"} onClick={()=> deleteProjects(project.project_id)}>Delete</button>
            </td>
        </tr>
        ))}
    </tbody>
  </table>
    </Fragment>
}

export default ListProject;
