import React, { Fragment, useState } from 'react';
import styles from '../../styles/Home.module.css';

const InputProject = () => {

    const [project_company_name, setProject_Company_Name] = useState("");
    const [project_description, setProject_description] = useState("");
    const [project_location, setProject_location] = useState("");

    const onSubmitProject = async(e) => {
        e.preventDefault();
        try {
            const body = {project_company_name, project_description, project_location};
            const response = await fetch("http://localhost:5000/project",{
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });

            window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
    }

    return(
    <Fragment>
    {/*
        Hér á ég eftir að meðhöndla þetta með Bootstrap til að fegra aðeins útlit síðunnar
    */}
        <h1 className={styles.title}>Add new Project</h1>
        <form className={styles.container} onSubmit={onSubmitProject}> 
            <h2>Clock Timer Function displayed</h2>
            <p>Company Name</p>
            <input type="text" 
                value={project_company_name} 
                onChange={e => setProject_Company_Name(e.target.value)}
            />
            <p>Project Description</p>
            <input type="text" 
                value={project_description}
                onChange={e => setProject_description(e.target.value)}    
            />
            <p>Project Location</p>
            <input type="text" 
                value={project_location}
                onChange={e => setProject_location(e.target.value)}
            />
            <button className={styles.button}>Add</button>
        </form>
    </Fragment>
    )
}

export default InputProject;
